mapper.select:
SELECT o.id, t.start_station startStation,t.stop_station stopStation, u.user_name userName, o.num, o.state, o.order_num orderNum, o.create_time createTime FROM ticket_order as o LEFT JOIN ticket t ON (o.ticket_id=t.id) LEFT JOIN `user` u ON (o.user_id = u.id)
AND start_station LIKE CONCAT('%',#{startStation},'%')
AND stop_station LIKE CONCAT('%',#{stopStation},'%')
AND user_name LIKE CONCAT('%',#{userName},'%')
AND o.create_time >=#{minTime}
AND o.create_time<=#{maxTime}
LIMIT #{startIndex}, #{pageSize}

SELECT COUNT(1) FROM ticket_order as o LEFT JOIN ticket t ON (o.ticket_id=t.id) LEFT JOIN `user` u ON (o.user_id = u.id)
AND start_station LIKE CONCAT('%',#{startStation},'%')
AND stop_station LIKE CONCAT('%',#{stopStation},'%')
AND user_name LIKE CONCAT('%',#{userName},'%')
AND o.create_time >= #{minTime}
AND o.create_time<=#{maxTime}
